import React from 'react';
// import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      todos: {
        id: new Date().getTime(),
        itemTodo:'',
      },
      value: '',
      selectedItem:'',
    }
  }

    handleInputChange = (event) => {
      this.setState({ value: event.target.value })
    }

    handlebtnClick = (e) => {
      e.preventDefault()
      if(this.state.value !== ""){
          const newTodos = [...this.state.todos.itemTodo, this.state.value]
          this.setState({
            todos: newTodos
          })
          localStorage.setItem('todolist',JSON.stringify(newTodos))
          this.setState({value: ""})

      }
      // const { todos, value } = this.state
      // console.log('todos: ', todos)
      // console.log(',newTodo: ', value)
    }

    removeTodos = (index, e) => {
      e.preventDefault()
      const newTodos  = this.state.todos.filter(item => this.state.todos.indexOf(item) !== index)
		  this.setState({
        todos: newTodos
      })
      // console.log('newTodos value: ', newTodos)
		  localStorage.setItem('todolist',JSON.stringify(newTodos))
    }

    componentDidMount() {
      const listTodo = localStorage.getItem('todolist')
      const reset = JSON.parse(listTodo)
      this.setState({ todos: reset})
      console.log('get listTodo: ', listTodo)
    }

    btnUpdateClick(item,index,e) {
      // const popup = document.getElementById('popup')
      e.preventDefault()
      this.setState({
        selectedItem: item,
      })
    }

    UpdateItemClick = (value, e) => {
      console.log('value: ', value)

      e.preventDefault()
      console.log('todos updateItemClick: ', this.state.todos)
    }
    handleInputUpdate = (e) => {
      this.setState({selectedItem: e.target.value})
    }

    render(){
      // const { todos, value } = this.state
      // tuong duong voi:  const todos= this.state.todos
      // destructuring
      console.log('selectedItem: ', this.state.selectedItem)
      return (
        <div>
          <form className='form-todolist'>
            <input value={this.state.value} type='text' placeholder='todoList...' onChange={this.handleInputChange}>
            </input>
                <button onClick={(e) => { this.handlebtnClick(e)}}>
                Add
                </button>
            <div>
            {this.state.todos.map(item =>  
            <div className="item">
              <div className='value' key={item}>{item}</div>
              <button onClick={(e) => this.removeTodos(this.state.todos.indexOf(item),e)} className='del'>delete</button>
              <button className='update' onClick={(e) => this.btnUpdateClick(item,e)}>update</button>
            </div>	
            )}
          </div>

          <div id='popup' className='popup-update'>
            <input value={this.state.selectedItem} onChange={(e) => this.handleInputUpdate(e)}></input>
              <div className='btn-popup'>
                <button onClick={(e) => this.UpdateItemClick(this.state.selectedItem,e)}>Update</button>
                <button>Cancel</button>
              </div>
          </div>
          </form>
        </div>
      )
    }
}
export default App;
