import React, { useImperativeHandle } from 'react';
// import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [],
      // {
      //   id: new Date().getTime(),
      //   value: ''
      // }
      Item:{
        id: new Date().getTime(),
        valueItem: ''
      }
    }
  }

  handleInputChange = (e) => {
    e.preventDefault()
    this.setState({
      Item: {
        id: new Date().getTime(),
        valueItem: e.target.value
      }
    })
  }



  render() {
    console.log('value: ', this.state.Item.valueItem)
    console.log('todos: ', this.state.todos)
    return (
      <div className='form-todolist'>
        <input placeholder='todoList...' onChange={ (e) => this.handleInputChange(e) }></input>
        <button onClick={ (e) => this.handleBtnClick(e) }>Add</button>
      </div>

    )
  }
}

export default App;
