import React from 'react';
// import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      todos:[]
    }
    this.state = {
      value: ''
    }
    this.handleInputClick = this.handleInputClick.bind(this)
    this.handlebtnClick = this.handlebtnClick.bind(this)
  }
    handleInputClick = (e) => {
      setValue(event.target.value)
    }

    handlebtnClick = (e) => {
      e.preventDefault()
      if(this.state.value !== ""){
          const newTodos = [...this.todos, value]
          this.setState({
            todos: newTodos
          })
          localStorage.setItem('todolist',newTodos)
          setValue('')
      }
    }

    removeTodos = (index, e) => {
      e.preventDefault()
      const newTodo  = todos.filter(item => todos.indexOf(item) !== index)

		  setTodos(newTodo)
		  localStorage.setItem('todolist',todos)
    }
    render(){
      return (
        <div>
          <form className='form-todolist'>
            <input value={value} type='text' placeholder='todoList...' onChange={handleInputChange}>
            </input>
            <button onClick={(e) => { handlebtnClick(e)}}>
              Add
                </button>
            <div>
            {todos.map(item => 
            <div className="item">
              <div className='value' key={item}>{item}</div>
              <button onClick={(e) => removeTodos(todos.indexOf(item),e)} className='del'>delete</button>
              <button className='update'>update</button>
            </div>	
            )}
          </div>
          </form>
        </div>
      )
    }
}
export default App;
