import React from 'react';
// import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      todos: [],
      value: ''
    }
  }

    handleInputClick = (event) => {
      this.setState({ value: event.target.value })
    }

    handlebtnClick = (e) => {
      e.preventDefault()
      if(this.state.value !== ""){
          const newTodos = [...this.todos, this.value]
          this.setState({
            todos: newTodos
          })
          localStorage.setItem('todolist',newTodos)
          this.setState({value: ""})
      }
    }

    removeTodos = (index, e) => {
      e.preventDefault()
      const newTodo  = this.todos.filter(item => this.todos.indexOf(item) !== index)

		  this.setState({
        todos: newTodo
      })
		  localStorage.setItem('todolist',this.newTodo)
    }

    render(){
      const { todos } = state
      // destructuring
      console.log('todos: ', this.state.todos)
      console.log(',newTodo: ', this.newTodo)
      return (
        <div>
          <form className='form-todolist'>
            <input value={this.value} type='text' placeholder='todoList...' onChange={this.handleInputChange}>
            </input>
            <button onClick={(e) => { this.handlebtnClick(e)}}>
              Add
                </button>
            <div>
            {this.todos.map(item => 
            <div className="item">
              <div className='value' key={item}>{item}</div>
              <button onClick={(e) => this.removeTodos(this.todos.indexOf(item),e)} className='del'>delete</button>
              <button className='update'>update</button>
            </div>	
            )}
          </div>
          </form>
        </div>
      )
    }
}
export default App;
