import React, { Component } from 'react';
import './App.css';

class ListTodos extends React.Component {
   constructor(props) {
      super(props)
      
      this.state = {
         todosChild: props.isAddTodo
      }
   }
   componentWillReceiveProps = () => {
      console.log('isAdd todo: ', this.props.isAddTodo)
      const checkTodo = props.isAddTodo
      // const tempTodo = this.props.isAddTodo
      // const setTodo = [...tempTodo, this.props.isAddTodo]
      // console.log('setTodo: ', setTodo)
      if (checkTodo !== this.props.isAddTodo) {
         this.setState({
            todosChild: this.props.isAddTodo
         })
      }
      console.log('todosChild update: ', this.state.todosChild)
   }
   render() {
      console.log('isAddTodo: ', this.props.isAddTodo)
      console.log('todoChild: ', this.state.todosChild)
      return (
         <div>
            <div>student</div>
            {this.state.todosChild.map(item =>
               <div className='item'>
                  <div className='value' key={item.id}>
                     {item.valueItem}
                  </div>
                  <button className='delete' onClick={(e) => this.handleRemoveClick(item.id, e)}>delete</button>
                  <button className='update' onClick={(e) => this.handleUpdateClick(item.valueItem, e)}>update</button>
               </div>
            )}
         </div>
      )
   }
}
export default ListTodos;
