import React, { Component } from 'react';
import './App.css';

class ListTodos extends React.Component {
   constructor(props) {
      super(props) 
      this.state = {
         childTodos: props.isAddTodo
      }
      console.log('isAddTodo e: ', this.props.isAddTodo)
   }

   componentWillReceiveProps = (nextProps) => {
      // console.log('nextprops: ', nextProps)
      if(this.props.isAddTodo !== nextProps.isAddTodo){
         this.setState({
            childTodos: nextProps.isAddTodo
         })
      }
      // console.log('childTodos: ', this.state.childTodos)
   }

   render() {
      console.log('childTodos: ', this.state.childTodos)
      console.log('isAddTodo: ', this.props.isAddTodo)
      return (
         <div>
            {/* <div>student</div> */}
            {this.state.childTodos.map(item =>
               <div className='item'>
                  <div className='value' key={item.id}>
                     {item.valueItem}
                  </div>
                  <button className='delete' onClick={(e) => handleRemove(item.id, e)}>delete</button>
                  <button className='update' onClick={(e) => handleUpdate(item.valueItem, e)}>update</button>
               </div>
            )}
         </div>
      )
   }
}
export default ListTodos;
