import React, { useImperativeHandle } from 'react';
// import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [],
      // {
      //   id: new Date().getTime(),
      //   value: ''
      // }
      Item:{
        id: new Date().getTime(),
        valueItem: ''
      }
    }
  }

  handleInputChange = (e) => {
    e.preventDefault()
    this.setState({
      Item: {
        id: new Date().getTime(),
        valueItem: e.target.value
      }
    })
  }

  handleBtnClick = (e) => {
    e.preventDefault()
    const newTodos = [...this.state.todos, this.state.Item]
    this.setState({
      todos: newTodos
    })
    this.setState({
      Item: {
        id:''
      }
    })
    console.log('Item kia: ', this.state.Item)
    console.log('newTodos: ', newTodos)
    console.log('todo của toi: ', this.state.todos)
  }

  handleRemoveClick = (id, e) => {
    e.preventDefault()
    const newTodos = this.state.todos.map(item => this.state.todos.id !== id)
    this.setState({
      todos: newTodos
    })
  }

  render() {
    console.log('value: ', this.state.Item.valueItem)
    console.log('todos: ', this.state.todos)
    return (
      <div className='form-todolist'>
        <input placeholder='todoList...' onChange={ (e) => this.handleInputChange(e) }></input>
        <button className='add' onClick={ (e) => this.handleBtnClick(e) }>Add</button>
        {this.state.todos.map(item => 
          <div className='item'>
             <div className='value' key={item.id}>
             {item.valueItem}
            </div>
            <button className='delete' onClick={(e) => this.handleRemoveClick(this.state.todos.id, e)}>delete</button>
            <button className='update'>update</button>
          </div> 
        )}
      </div>
    )
  }
}

export default App;
