import React from 'react'
import './App.css'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      todos:[{
        id: new Date().getTime(),
        value: ''
      }],
      item:{
        id: new Date().getTime(),
        valueItem: '',
      }
    }
  }
  handleInputChange = (e) => {
    e.preventDefault()
    this.setState({
      item:{
        id: new Date().getTime(),
        valueItem: e.target.value
      }
    })
    // console.log('valueItem: ', this.state.item.valueItem)
  }

  handlebtnClick = (e) => {
    e.preventDefault()
    console.log('value')
    const newTodos = [...this.state.todos, this.state.item.value]
    this.setState({
      todos: newTodos
    })
    localStorage.setItem('todolist', JSON.stringify(newTodos))
    // this.setState({
    //   item:{
    //     id:0,
    //     valueItem:''
    //   }
    // })
    console.log('todos kia: ', this.state.todos)
  }

  render() {
    return (
      <div className='form-todolist'>
        <input placeholder='todoList' onChange={(e) => this.handleInputChange(e)}></input>
        <button onClick={(e) => this.handlebtnClick(e)}>Add</button>
      </div>
    )
  }
}
export default App
