import React, { useImperativeHandle } from 'react';
// import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [],
      valueInput:'',
      valueUpdate:'',
      Item:{
        id: new Date().getTime(),
        valueItem: ''
      }
    }
  }

  handleInputChange = (e) => {
    e.preventDefault()
    this.setState({
      valueInput: e.target.value,
    })

  }

  handleBtnClick = (e) => {
    e.preventDefault()
    // console.log('valueItem before: ', this.state.Item.valueItem)
    // const newTodo = [...this.state.todos, this.state.Item]
    const newTodo = {
      id: new Date().getTime(),
      valueItem: this.state.valueInput
    }
    const newTodos = [...this.state.todos, newTodo]
    this.setState({
      Item:{
        id: new Date().getTime(),
        valueItem: this.state.valueInput
      },
      todos: newTodos,
      valueInput:'',
    })

  }

  handleRemoveClick = (id, e) => {
   const todoDel = this.state.todos.filter(item => item.id !== id)
   this.setState({
     todos: todoDel,
   })
  }

  handleUpdateClick = (id,e) => {
    e.preventDefault()
    const getNode = this.state.todos.filter(item => item.id === id)
    console.log('up: ', getNode.valueItem)
    this.setState({
      valueUpdate: getNode.valueItem
    })
  }

  handleUpdateChange = (e) => {
    e.preventDefault()
    this.setState({
      valueUpdate: e.target.value
    })
  }

  clickUpdate = (e) => {

  }

  render() {
    console.log('valueInput: ', this.state.valueInput)
    console.log('valueItem: ', this.state.Item.valueItem)
    console.log('Item: ', this.state.Item)
    console.log('todos: ', this.state.todos)
    return (
      <div className='form-todolist'>
        <input value={this.state.valueInput} placeholder='todoList...' onChange={ (e) => this.handleInputChange(e) }></input>
        <button className='add' onClick={ (e) => this.handleBtnClick(e) }>Add</button>
        {this.state.todos.map(item => 
          <div className='item'>
             <div className='value' key={item.id}>
             {item.valueItem}
            </div>
            <button className='delete' onClick={(e) => this.handleRemoveClick(item.id, e)}>delete</button>
            <button className='update' onClick = {(e) => this.handleUpdateClick(item.id, e)}>update</button>
          </div> 
        )}

        <div className='panel-update'>
          <input value={this.state.valueUpdate} onChange={(e) => this.handleUpdateChange(e)}></input>
          <div className='btn-update'>
            <button onClick={(e) => this.clickUpdate(e) }>Update</button>
            <button>Cancel</button>
          </div>
        </div>
      </div>
    )
  }
}

export default App;
